import React, { useState, useEffect } from 'react';
import './styles.css'; // Optional: Add custom CSS styles

function App() {
    // State for handling file uploads
    const [file, setFile] = useState(null);
    const [uploadMessage, setUploadMessage] = useState('');

    // State for storing uploaded photos
    const [photos, setPhotos] = useState([]);

    // Fetch uploaded photos from the backend on component mount
    useEffect(() => {
        fetch('http://localhost:5000/photos')
            .then((response) => response.json())
            .then((data) => setPhotos(data.photos))
            .catch((err) => console.error('Error fetching photos:', err));
    }, []);

    // Handle photo upload submission
    const handleUploadSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('photo', file);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await response.json();
            setUploadMessage(data.message);

            // Refresh the photo list after successful upload
            if (response.ok) {
                fetch('http://localhost:5000/photos')
                    .then((response) => response.json())
                    .then((data) => setPhotos(data.photos));
            }
        } catch (error) {
            setUploadMessage('Error uploading photo');
        }
    };

    return (
        <div>
            {/* Header Section */}
            <header style={{ textAlign: 'center', padding: '1rem', backgroundColor: '#333', color: '#fff' }}>
                <h1>Photo Exhibition Platform</h1>
            </header>

            {/* Upload Form Section */}
            <div style={{ maxWidth: '600px', margin: '20px auto', textAlign: 'center' }}>
                <h2>Upload a Photo</h2>
                <form onSubmit={handleUploadSubmit}>
                    <input
                        type="file"
                        onChange={(e) => setFile(e.target.files[0])}
                        style={{ marginBottom: '10px' }}
                    />
                    <br />
                    <button
                        type="submit"
                        style={{
                            backgroundColor: '#333',
                            color: 'white',
                            padding: '10px 20px',
                            border: 'none',
                            cursor: 'pointer',
                            borderRadius: '5px',
                        }}
                    >
                        Upload
                    </button>
                </form>
                {uploadMessage && <p>{uploadMessage}</p>}
            </div>

            {/* Photo Gallery Section */}
            <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
                <h2 style={{ textAlign: 'center' }}>Gallery</h2>
                <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                    {photos.map((photo, index) => (
                        <img
                            key={index}
                            src={photo.url}
                            alt={`Uploaded ${index}`}
                            style={{
                                width: '200px',
                                margin: '10px',
                                borderRadius: '10px',
                                boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
                            }}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
